parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "AI Project Manager Output",
  "description": "Schema to validate the structured output from the AI project manager agent.",
  "type": "object",
  "required": [
    "assessment",
    "actions"
  ],
  "properties": {
    "assessment": {
      "type": "object",
      "description": "The AI's summary of the current project state.",
      "required": [
        "timestamp",
        "status",
        "summary",
        "reviewed_documents",
        "reviewed_summaries"
      ],
      "properties": {
        "timestamp": {
          "type": "string",
          "format": "date-time",
          "description": "ISO 8601 timestamp of when the assessment was made."
        },
        "status": {
          "type": "string",
          "description": "The status of the assessment run.",
          "enum": [
            "complete",
            "error"
          ]
        },
        "summary": {
          "type": "string",
          "description": "A one-sentence summary of the project's current state."
        },
        "reviewed_documents": {
          "type": "array",
          "description": "List of design document paths that were reviewed.",
          "items": {
            "type": "string"
          }
        },
        "reviewed_summaries": {
          "type": "array",
          "description": "List of agent summary paths that were reviewed.",
          "items": {
            "type": "string"
          }
        }
      }
    },
    "actions": {
      "type": "object",
      "description": "A list of proposed actions to be taken on GitHub.",
      "required": [
        "update_github_project",
        "create_github_issue",
        "follow_up_github_issue"
      ],
      "properties": {
        "update_github_project": {
          "type": "array",
          "description": "Tasks to update on the GitHub project board.",
          "items": {
            "type": "object",
            "required": [
              "task_id",
              "new_status",
              "comment"
            ],
            "properties": {
              "task_id": {
                "type": "string"
              },
              "new_status": {
                "type": "string"
              },
              "comment": {
                "type": "string"
              }
            }
          }
        },
        "create_github_issue": {
          "type": "array",
          "description": "New issues to be created.",
          "items": {
            "type": "object",
            "required": [
              "title",
              "body"
            ],
            "properties": {
              "title": {
                "type": "string"
              },
              "body": {
                "type": "string"
              },
              "labels": {
                "type": "array",
                "items": {
                  "type": "string"
                }
              },
              "assignee": {
                "type": "string"
              }
            }
          }
        },
        "follow_up_github_issue": {
          "type": "array",
          "description": "Existing issues that require a follow-up comment.",
          "items": {
            "type": "object",
            "required": [
              "issue_number",
              "comment"
            ],
            "properties": {
              "issue_number": {
                "type": "integer"
              },
              "comment": {
                "type": "string"
              }
            }
          }
        }
      }
    }
  }
}
